import React, { Component } from 'react';
import axios from 'axios';

export default class GetRecentDistance extends Component {
  constructor() {
    super();
    this.state = {
      distance: 'Null',
    };
  }

  componentDidMount = () => {
    axios.get('https://asia-southeast2-beacon-run.cloudfunctions.net/app/runs?userId=$U65543e5b34c1a89fb0078c5193ae6621$&limit=1').then((response) => { 
      this.setState({
        distance: response.data.temp_c,
      });
    });
  };

  render() {
    const dist = (this.state.distance); //eslint-disable-line
    return (
      <div className="mobile-app-widget__top-line mobile-app-widget__top-line--lime">
        <p className="mobile-app-widget__total-stat">{ dist }</p>
      </div>
    );
  }
}
